function Comecar_TDAH(){
    pergunta.style.textIndent="20px";
    escrever_pergunta=window.document.querySelector("#questionario");
    escrever_pergunta.innerHTML=questions[questao_atual];
    questao_atual++;
    resposta=window.document.querySelector("#resposta");
    resposta.style.backgroundColor="transparent";
    botao_sim=document.createElement('button');
    botao_sim.textContent='Sim';
    botao_sim.id="sim";
    botao_nao=document.createElement('button');
    botao_nao.textContent='Não';
    botao_nao.id="nao";
    resposta.appendChild(botao_sim);
    resposta.appendChild(botao_nao);
    botao_sim.onclick=()=>{
        Avancar('sim');
    };
    botao_nao.onclick=()=>{
        Avancar('nao');
    }
    botao_nao.onmouseenter=Mudar_cor_entrou_nao;
    botao_nao.onmouseleave=Mudar_cor_saiu_nao;
    botao_sim.onmouseenter=Mudar_cor_entrou_sim;
    botao_sim.onmouseleave=Mudar_cor_saiu_sim;
}

